import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import { Playground } from 'docz'
import message from './index'
import Button from '../Button'

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "drawer"
    }}>{`Drawer`}</h1>
    <h4 {...{
      "id": "基本用法"
    }}>{`基本用法`}</h4>
    <Playground __position={0} __code={'<Button\n  onClick={() => {\n    message.pop({\n      content: \'打卡\',\n    })\n  }}\n>\n  默认通知信息\n</Button>\n<br />\n<Button\n  type=\"primary\"\n  onClick={() => {\n    message.pop({\n      type: \'success\',\n      content: \'你的请求被审批通过啦！\',\n      duration: 0,\n    })\n  }}\n>\n  成功通知(success)\n</Button>\n<br />\n<Button\n  type=\"pure\"\n  onClick={() => {\n    message.pop({\n      type: \'info\',\n      content: \'成功信息通知\',\n    })\n  }}\n>\n  信息通知(info)\n</Button>\n<br />\n<Button\n  type=\"primary\"\n  onClick={() => {\n    message.pop({\n      type: \'warning\',\n      content: \'警告信息通知\',\n    })\n  }}\n>\n  警告通知(warning)\n</Button>\n<br />\n<Button\n  type=\"warning\"\n  onClick={() => {\n    message.pop({\n      type: \'error\',\n      content: \'错误信息通知\',\n    })\n  }}\n>\n  错误信息通知(error)\n</Button>'} __scope={{
      props: this ? this.props : props,
      Playground,
      message,
      Button
    }} __codesandbox={`undefined`} mdxType="Playground">
    <Button onClick={() => {
        message.pop({
          content: '打卡'
        });
      }} mdxType="Button">默认通知信息</Button><br />
    <Button type="primary" onClick={() => {
        message.pop({
          type: 'success',
          content: '你的请求被审批通过啦！',
          duration: 0
        });
      }} mdxType="Button">成功通知(success)</Button><br />
    <Button type="pure" onClick={() => {
        message.pop({
          type: 'info',
          content: '成功信息通知'
        });
      }} mdxType="Button">信息通知(info)</Button><br />
    <Button type="primary" onClick={() => {
        message.pop({
          type: 'warning',
          content: '警告信息通知'
        });
      }} mdxType="Button">警告通知(warning)</Button><br />
    <Button type="warning" onClick={() => {
        message.pop({
          type: 'error',
          content: '错误信息通知'
        });
      }} mdxType="Button">错误信息通知(error)</Button>
    </Playground>
    <h2 {...{
      "id": "使用"
    }}>{`使用`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-js"
      }}>{`import { message } from 'cf-design'

// 使用
message.pop({
    type: 'warning',
    content: '内容',
    duration: 0
})

// 全局配置
message.config({
    duration: 6
})
`}</code></pre>
    <h2 {...{
      "id": "api文档"
    }}>{`API文档`}</h2>
    <h3 {...{
      "id": "message的全局属性"
    }}>{`Message的全局属性`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`参数`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`说明`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`类型`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`默认值`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`duration`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`默认自动关闭延时，单位秒`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`number`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`4.5s`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`getContainer`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`HTMLNode 配置渲染节点的输出位置`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`() => HTMLNode`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`() => document.body`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`closeIcon`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`自定义关闭图标`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`HTMLNode`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
      </tbody>
    </table>
    <h3 {...{
      "id": "notice属性"
    }}>{`notice属性`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`参数`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`说明`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`类型`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`默认值`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`type`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`通知窗类型(info,success,error,warning)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`btn`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`自定义关闭按钮`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`ReactNode`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`className`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`自定义内容的class`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`duration`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`默认 4.5 秒后自动关闭，配置为 null 则不自动关闭`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`number`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`4.5`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`closeIcon`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`自定义关闭图标`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`ReactNode`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`getContainer`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`配置渲染节点的输出位置`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`HTMLNode`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`icon`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`自定义图标`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`ReactNode`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`key`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`当前通知唯一标志`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`content`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`通知提醒标题，必写`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string,ReactNode`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`onClose`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`点击默认关闭按钮时触发的回调函数`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`(e) => void`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`onClick`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`点击通知时触发的回调函数`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`(e) => void`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`top`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`消息从顶部弹出时，距离顶部的位置，单位像素`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
      </tbody>
    </table>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
